var Create = {
    nextPage: function() {
        var result = false;
        try {
            var next = eval(下一页);
            //log('n:'+next)
            if (next == "") {
                throw new Error('下一页链接为空')
            }
            result = true;
            if (true_url == next) {
                result = false;
            }
        } catch (e) {
            //log("可能不存在下一页或者下一页定位有问题");
            log("错误信息:" + e.message);
        }
        if ((next == "" || next == undefined) && page == 1) {
            next = true_url;
        } else {
            //result=false;
            next = getMyVar(状态);
        }
        //log('l'+true_url)
        //log('n'+next)
        if (page != 1) {
            if (true_url == next && !result) {
                result = false;
            } else {
                result = true;
            }
        }
        return result;
        //putMyVar(状态, next);
    },
    getBackImage: function(imgurl) {
        var result = imgurl;
        try {
            result = /url(?:\s+)?\((.*?)\)/g
                .exec(imgurl)[1]
                .replace(/('|"|&quot;)/gm, "");
        } catch {}
        return result;
    },
    httpHandle: function(input) {
        if (input.startsWith("http")) {
            return input;
        } else if (input.startsWith("//")) {
            return "https:" + input;
        } else if (input.startsWith("/")) {
            return MY_HOME + input;
        }
    },
    imgHandle: function(item, rule) {
        var img = "";
        if (rule.img.startsWith("pd")) {
            img = eval(rule.img);
        } else if (rule.backimg) {
            img = pdfh(item, rule.img);
            img = this.getBackImage(img);
            img = this.httpHandle(img);
        } else {
            img = pdfh(item, rule.img);
            if (!img.startsWith("http")) {
                img = pd(item, rule.img);
                img = this.httpHandle(img);
            }
        }
        return img + rule.ref;
    },
    urlHandle: function(item, rule) {
        var href = "";
        if (rule.url.startsWith("pd")) {
            href = eval(rule.url);
        } else {
            href = pd(item, rule.url);
        }
        href = rule.imt ? href + "#immersiveTheme##autoCache#" : href;
        return href;
    },
    setContent: function(item, input) {
        let output = "";
        if (input == undefined) {
            return output;
        }
        if (input.startsWith("pd")) {
            output = eval(input);
        } else {
            output = pdfh(item, input);
        }
        return output;
    },
    refresh: function() {
        addListener(
            "onRefresh",
            $.toString((u) => {
                clearMyVar("yilist");
                //var p = storage0.getMyVar('spage', '');
                //putMyVar(u, p);
            }, 状态)
        );
    },
    getBooleanValue: function(value, defaultValue) {
        return value !== undefined ? Boolean(value) : (defaultValue !== undefined ? defaultValue : true);
    },
    debuglog: function(i, item, rule, obj) {
        log('----------' + (i + 1) + ':调试开始----------');
        if (item.constructor == Object) {
            item = JSON.stringify(item);
        }
        log('item内容:\r\n' + item + '\r\n' +
            `\n\ttitle定位:  ${rule.title}  ---> 结果:  ${obj.title}` +
            `\n\tdesc定位:  ${rule.desc}  ---> 结果:  ${obj.desc}` +
            `\n\timg定位:  ${rule.img}  ---> 结果:  ${obj.img}` +
            (rule.content != undefined ? `\n\tcontent定位:  ${rule.content}  ---> 结果:  ${obj.content}` : '') +
            `\n\turl定位:  ${rule.url}  ---> 结果:  ${obj.url}`
        );
        log('----------' + (i + 1) + ':调试结束----------');
    },
    jsonList: function(arr, json, rule) {
        if (json.constructor != Object) {
            try {
                json = JSON.parse(json);
            } catch {}
        }
        if (json[rule.list] == undefined) {
            arr.push({
                title: '““””<font color="grey"><small>无内容</small></font>',
                url: "hiker://empty",
                col_type: "text_center_1",
                extra: {},
            });
            return;
        }
        var debug = this.getBooleanValue(rule.debug, false);
        var empty = this.getBooleanValue(rule.empty);
