    //let picUrl = picUrl;
    picUrl.match(/photos\/(\d+)?\/(\d+)?/);
    let bookId = RegExp.$1;
    let imgId = RegExp.$2;
    if(!bookId||!imgId||Number(bookId)<=220980) return imgInputStream;
    if (Number(bookId) >= "268850" && Number(bookId) <= "421925") {
    var $num = parseInt(md5(bookId + imgId).slice(-1).charCodeAt() % 10) * 2 + 2;
