function countChars(str) {
    const chineseRegex = /[\u4e00-\u9fa5]/g; // åŒ¹é…ä¸­æ–‡å­—ç¬¦
    const englishRegex = /[a-zA-Z]/g; // åŒ¹é…è‹±æ–‡å­—ç¬¦
    const numberRegex = /\d/g; //åŒ¹é…æ•°å­—
    const chineseCount = (str.match(chineseRegex) || []).length;
    const englishCount = (str.match(englishRegex) || []).length;
    const numberCount = (str.match(numberRegex) || []).length;
    return {
        chineseCount,
        englishCount,
        numberCount,
    };
}


