            data[key] = {};
          }
          return data[key][path] != undefined ? data[key][path] : {};
        } else {
          return data[key] != undefined ? data[key] : {};
        }
      },
      set: function (key, value, path) {
        var type = typeof (this.self[path]);
        switch (type) {
          case "string":
            break;
          case "number":
            value = parseInt(value);
            break;
          case "boolean":
            break;
