function UrlProcessor() {
    this.params = {}
    this.regexMap = [
        {
            reg: 'vodtype\\/(\\d+)',
            exec: () => {
                return this.params.url
                    .replace(/vodtype(\/\d+)\.html/, 'vodshow$1-----------.html')
