function setDesc(arr, desc) {
    function cutstr(str, len) {
        var str_length = 0;
        var str_len = 0;
        str_cut = new String();
        str_len = str.length;
        for (var i = 0; i < str_len; i++) {
            a = str.charAt(i);
            str_length++;
            if (escape(a).length > 4) {
                //中文字符的长度经编码之后大于4
                str_length++;
            }
            str_cut = str_cut.concat(a);
            if (str_length >= len) {
                //达到目标长度，即为字符串加上省略号并返回
                str_cut = str_cut.concat("...");
                return str_cut;
            }
        }
        //如果字符串长度小于需要裁切的长度，直接返回
        if (str_length < len) {
            return str;
        }
    }
    desc=desc.constructor==Array?desc.join(''):desc;
    if (desc.replace(/(<br>|\s+)/g, '').length == 0) {
        return
    }
    desc = desc.startsWith('　　') ? desc : '　　' + desc;
    desc = '<small>' + desc + '</small>';
    let sdesc = cutstr(desc, 111);
    arr.push({
        title: '剧情简介<span style="color:#FF1493;"><b><small>' + storage0.getItem('dshow', '展开') + '</small></b></span>:',
        col_type: 'avatar',
        img: 'https://hikerfans.com/tubiao/system/75.png',
        url: $('#noLoading#').lazyRule((dc, sdc) => {
            var ds = storage0.getItem('dshow', '展开');
            var title = findItem('dshow').title;
            if (ds == '展开') {
                storage0.setItem('dshow', '收起')
                updateItem('dcontent', {
                    title: dc,
                });

                updateItem('dshow', {
                    title: title.replace('展开', '收起')
                });

                updateItem
            } else {
                storage0.setItem('dshow', '展开')
                updateItem('dcontent', {
                    title: sdc,
                });

                updateItem('dshow', {
                    title: title.replace('收起', '展开')
                });
            }
            return 'hiker://empty'
        }, desc, sdesc),
        extra: {
            id: 'dshow',
        }
    })
    arr.push({
        title: storage0.getItem('dshow', '展开') == '展开' ? sdesc : desc,
        col_type: 'rich_text',
        extra: {
            id: 'dcontent',
        }
    })
}

