function search_360(key_word) {
    // $.log(key_word)
    let res_360 = []

    let html = fetch('https://api.so.360kan.com/index?kw=' + key_word, {
        headers: {
            Referer: 'https://video.360kan.com/',
            'User-Agent':
                'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/122.0.0.0 Safari/537.36',
        },
    })

    html = JSON.parse(html)
    // log(html)
    // html.data.longData.length
    if (html.data.longData.rows) {
        for (let item of html.data.longData.rows) {
            let res_item = {}
            res_item.cat_id = item.cat_id
            res_item.en_id = item.en_id
            res_item.site = item.playlinks
            res_item.title = item.titleTxt
            res_item.cat_name = item.cat_name
            res_item.category = item.cat_name
            res_item.info = item.coverInfo.txt ? item.coverInfo.txt : ''
            res_360.push(res_item)
        }
    }

    return res_360
}

function get_offical_link(item, episode) {
    // $.log(item)
    // $.log(episode)

    let offical_link = ''

    let param_s = encodeURIComponent(
        '[{"cat_id":"' + item.cat_id + '","ent_id":"' + item.en_id + '","site":"' + item.site + '"}]'
    )

    let html = fetch('https://api.so.360kan.com/episodesv2?v_ap=1&s=' + param_s + '&cb=__jp5', {
        headers: {
            Referer: 'https://video.360kan.com/',
            'User-Agent':
                'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/122.0.0.0 Safari/537.36',
        },
    })

    html = html.match(/__jp.*?\((.*?)\);/)[1]
    html = JSON.parse(html)

    if (html.data.length == 0) {
        offical_link = item.playlinks[item.site]
    } else {
        offical_link = html.data[0].seriesHTML.seriesPlaylinks[episode - 1].url
    }

    return $.log(offical_link)
}
