    // 输出提取到的域名
    if (domains && domains.length > 0) {
        log('可用域名列表: ' + domains.join(', '));
        // 遍历并验证连通性
        for (let i = 0; i < domains.length; i++) {
            const domain = domains[i];
            // 发起网络请求
            let response = JSON.parse(fetch(domain, { withStatusCode: true }));
            // log(JSON.stringify(response));
            if (response.statusCode === 200 && response.headers['content-type'].includes('text/html')) {
                log(domain + ' 连通且可用');
                settingContent.domain = domain;
                saveFile(settingFile, JSON.stringify(settingContent));
                // 存储获取到的网站首页源码信息
                putMyVar('indexCode', response.body);
                break;
            } else {
                log(domain + '不可用');
            }
        }
    } else {
        toast('未找到可用域名');
    }
}
try {
    if (!fileExist(settingFile)) {
        initDomain();
    } else {
        let domain = JSON.parse(request(settingFile)).domain;
        log('当前域名：' + domain)
        let response = JSON.parse(fetch(domain, { withStatusCode: true }));
        if (response.statusCode === 200 && response.headers['content-type'].includes('text/html')) {
            putMyVar('indexCode', response.body);
        } else {
            toast('域名失效...')
            initDomain();
        }
    }

} catch (e) {
    log(e.message);
}

