addListener('onClose', $.toString(() => {
    clearMyVar('tabName');
    clearMyVar('shsort');
    clearMyVar('lastTab');
}))
var d = [];
var lazy = $.require("lazy");
if (!MY_PARAMS.url) {
    // 轮播图点击
    let domain = JSON.parse(request(getMyVar('settingFile'))).domain;
    var currentUrl = domain + '/play/' + getParam('type') + '/' + getParam('id') + '.html';
} else {
    var currentUrl = MY_PARAMS.url;
}
log('当前链接：'+currentUrl);
// https://www.danmudy.com/play/ctid/OUPkaZ7kNY7vDj.html
var htmlCode = fetch(currentUrl);
// log(htmlCode);
if (MY_PARAMS.img) {
    var pic = MY_PARAMS.img
} else {
    itemInfo = pdfh(htmlCode, 'body&&script&&Html');
    const videoInfoRegex = /videoInfo\s*=\s*\{([\s\S]*?)\}/;
    const videoInfoMatch = itemInfo.match(videoInfoRegex)[0];
    eval(videoInfoMatch);
    var pic = videoInfo.image;
}
// 简介、演员、封面等
var title = MY_PARAMS.title;
var desc = pdfh(htmlCode, 'meta[name=description]&&content');
d.push({
    title: title,
    desc: desc,
    img: pic + '@Referer=',
    url: 'hiker://page/setting?rule=dm盒子#noHistory#',
    col_type: 'movie_1_vertical_pic_blur'
});
// 线路选集
// log(pdfa(htmlCode, 'body&&.am-nav-tabs&&li'));
tagFlag = pdfa(htmlCode, 'body&&.am-nav-tabs&&li').length;
switch (tagFlag) {
    case 0:
        // 线路下无分集-电影——可能卡顿导致的误判，不管了
        d.push({
            title: '““””<strong>☑️电影在线</strong>'.big(),
            url: 'hiker://empty',
            col_type: 'flex_button'
        }, {
            col_type: 'line_blank'
        });
        pdfa(htmlCode, 'body&&.btn-play-source').forEach(r => {
            d.push({
                title: pdfh(r, 'button&&Text'),
                url: pdfh(r, 'button&&data-url') + lazy,
                col_type: 'flex_button'
            });
        });
        break;
    default:
        // 线路下有分集-电视剧、动漫、综艺
        tabInfo = pdfa(htmlCode, 'body&&.am-nav-tabs&&li').map(t => {
            return {
                id: pdfh(t, 'a&&href'),
                name: pdfh(t, 'a&&Text'),
                link: currentUrl,
                data: []
            }
        });
        tabInfo.forEach(t => {
            tabId = t.id;
            // 多线路【网页可能出现读取中...】
            part = pdfa(htmlCode, `${tabId}&&button`);
            // log(part);
            if (part.length == 0) {
                log('当前线路获取数据失败...')
                part = pdfa(htmlCode, `body&&${tabId}`)[1];
                part = pdfa(part, 'div&&button');
            }
            if (getMyVar('shsort', '1') == '1') {
                part.forEach(r => {
                    t.data.push({
                        title: pdfh(r, 'button&&Text'),
                        url: pdfh(r, 'button&&data-url')
                    })
                })
            } else {
                part.forEach(r => {
                    t.data.unshift({
                        title: pdfh(r, 'button&&Text'),
                        url: pdfh(r, 'button&&data-url')
                    })
                })
            }
        });

        selectTab = getMyVar('tabName', tabInfo[0].name);
        storage0.putMyVar('tabInfo', tabInfo);
        // 线路列表
        d.push({
            title: (getMyVar('shsort') == '1') ? '““””<b><span style="color: #FF0000">逆序</span></b>' : '““””<b><span style="color: #1aad19">正序</span></b>',
            url: $().lazyRule((selectTab) => {
                putMyVar('shsort', getMyVar('shsort', '1') == '1' ? '0' : '1');
                updateItem('shayv_sort', { title: (getMyVar('shsort') == '1') ? '““””<b><span style="color: #FF0000">逆序</span></b>' : '““””<b><span style="color: #1aad19">正序</span></b>' });
                // refreshPage();
                tabInfo = storage0.getMyVar('tabInfo');
                deleteItemByCls('shayv_item');
                // 更新数据
                toUpdata = [];
                var lazy = $.require("lazy");
                tabInfo.forEach(t => {
                    t.data.reverse();
                })
                storage0.putMyVar('tabInfo', tabInfo);
                tabDetail=tabInfo.find(t => t.name === getMyVar('tabName', selectTab));
                link=tabDetail.link;
                tabId=tabDetail.id.slice(1);
                tabData=tabDetail.data;
                if(tabData.length==0){
                    log('线路数据缺失,正在通过接口发送请求...');
                    tabData=$.require("hiker://page/funs").getLists(link, tabId);
                    if(getMyVar('shsort') == '1'){
                        tabData.reverse();
                    }
                }
                tabData.forEach(r => {
                    toUpdata.push({
                        title: r.title,
                        url: r.url + lazy,
                        col_type: 'flex_button',
                        extra: {
                            cls: 'shayv_item'
                        }
                    });
                });
                addItemAfter('after_flag', toUpdata);
                return 'toast://切换排序成功'
            }, selectTab),
            col_type: 'flex_button',
            extra: {
                id: 'shayv_sort'
            }
        })
        var index = 1;
        tabInfo.forEach(t => {
            tabIndex = 'tab_' + index;
            d.push({
                title: t.name === selectTab ? `““””<strong>☑️${t.name}</strong>`.big() : t.name,
                url: $('#noLoading#').lazyRule((tabName, tabIndex) => {
                    putMyVar('tabName', tabName);
                    // refreshPage();
                    // 选择当前
                    updateItem(tabIndex, { title: `““””<strong>☑️${tabName}</strong>`.big() });
                    // 取消上一个选择
                    lastTab = findItem(getMyVar('lastTab', 'tab_1'));
                    updateItem(lastTab.extra.id, { title: lastTab.extra.title });
                    deleteItemByCls('shayv_item');
                    // 更新数据
                    toUpdata = [];
                    var lazy = $.require("lazy");
                    tabInfo = storage0.getMyVar('tabInfo');
                    tabDetail=tabInfo.find(t => t.name === getMyVar('tabName', tabName));
                    link=tabDetail.link;
                    tabId=tabDetail.id.slice(1);
                    tabData=tabDetail.data;
                    if(tabData.length==0){
                        log('线路数据缺失,正在通过接口发送请求...');
                        tabData=$.require("hiker://page/funs").getLists(link, tabId);
                        if(getMyVar('shsort') == '0'){
                            tabData.reverse();
                        }
                    }
                    tabData.forEach(r => {
                        toUpdata.push({
                            title: r.title,
                            url: r.url + lazy,
                            col_type: 'flex_button',
                            extra: {
                                cls: 'shayv_item'
                            }
                        });
                    });
                    addItemAfter('after_flag', toUpdata);
                    // 保存当前Tab的线路
                    putMyVar('lastTab', tabIndex);
                    return 'toast://已切换线路：' + tabName;
                }, t.name, tabIndex),
                col_type: 'flex_button',
                extra: {
                    id: tabIndex,
                    // cls:'shayv_item',
                    title: t.name
                }
            });
            index++;
        })
        d.push({
            title: '更多资源🔍️',
            url: 'hiker://search?s='+title,
            col_type: 'flex_button'
        },{
            col_type: 'line',
            extra: {
                id: 'after_flag'
            }
        })
        // 数据展示
        tabInfo.find(t => t.name === selectTab).data.forEach(r => {
            d.push({
                title: r.title,
                url: r.url + lazy,
                col_type: 'flex_button',
                extra: {
                    cls: 'shayv_item'
                }
            });
        });
        break;
}
