function aesecbde(word, keyy) {
    const CryptoUtil = $.require("hiker://assets/crypto-java.js");
    let key = CryptoUtil.Data.parseUTF8(keyy);
    let decrypted = CryptoUtil.AES.decrypt(word, key, {
        mode: "AES/ECB/PKCS7Padding"
    });
    return decrypted.toString()
}

function aesecben(word, keyy) {
    const CryptoUtil = $.require("hiker://assets/crypto-java.js");
    let key = CryptoUtil.Data.parseUTF8(keyy);
    let encrypted = CryptoUtil.AES.encrypt(word, key, {
        mode: "AES/ECB/PKCS7Padding"
    });
    return encrypted.toString()
}

function req1(url1, body1) {
    var html1 = request(host1+url1, {
        headers: header1,
        body: body1,
        method: "POST"
    }); //log(html1)
    return JSON.parse(aesecbde(html1, key1))
}

function req2(url1, keyy) {
    var html1 = request(url1, {
        headers: header1,
        method: "GET"
    }); //log(html1)
    return JSON.parse(aesecbde(html1, keyy))
}

