let size0 = 10;
let rulesText0 = `{RULES}`;
let rulesText = getItem("rules", rulesText0);
let sp = $.require("hiker://page/sp");
rules = rulesText.split("\n")
    .filter(it => it.includes("@"))
    .map(rule => {
        let s = rule.split("@");
        return {
            name: s[0],
            find: function(key, page) {
                return sp(s[1], key, s[0]);
            },
            url: s[1]
        };
    });

function load(page, rule) {
    let size = page <= 0 ? rules.length : size0;
    page = page <= 0 ? 1 : page;
    if (!page && !rule) {
        return rules.map(it => it.name);
    }
    if (rule != null) {
        if (page != null && page > 1) {
            return rules.filter(i => i.name == rule && i.page);
        }
        return rules.filter(i => i.name == rule);
    }
    let start = (page - 1) * size;
    if (rules.length < start + 1) {
        return [];
    }
    let len = size;
    if (rules.length < page * size) {
        len = rules.length - start
    }
    return rules.slice(start, start + len);
}

function addRule(name, surl) {
    setItem("rules", rulesText + "\n" + name + "@" + surl);
}

function removeRule(name) {
    let newT = rulesText.split("\n")
        .filter(it => it.indexOf(name + "@") < 0)
        .join("\n");
    setItem("rules", newT);
}

